    private fun generateQRCode(inputValue: String): ByteArray {
        val qrgEncoder = QRGEncoderKt(inputValue, null, QRGContentsKt.Type.TEXT, 800)
        qrgEncoder.colorBlack = Color.BLACK
        qrgEncoder.colorWhite = Color.TRANSPARENT

        val stream = ByteArrayOutputStream()
        qrgEncoder.bitmap!!.compress(Bitmap.CompressFormat.PNG, 100, stream)
        val byteArray: ByteArray = stream.toByteArray()
        qrgEncoder.bitmap!!.recycle()
        return byteArray
    }
